const techItems = [
  { name: 'Gemini', icon: GeminiIcon },
  { name: 'OpenAI', icon: OpenAIIcon },
  { name: 'Claude', icon: ClaudeIcon },
  { name: 'TypeScript', icon: TypeScriptIcon },
  { name: 'React', icon: ReactIcon },
  { name: 'Google', icon: GoogleIcon },
  { name: 'AWS', icon: AWSIcon },
  { name: 'GCP', icon: GCPIcon },
]

interface TechOrbitProps {
  className?: string
}

export default function TechOrbit({ className = '' }: TechOrbitProps) {
  const [isPaused, setIsPaused] = useState(false)
  const radius = 140
  const duration = 30

  return (
    <div
      className={`relative ${className}`}
      style={{ width: (radius + 50) * 2, height: (radius + 50) * 2 }}
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      {/* Outer decorative ring */}
      <div
        className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 rounded-full border border-dashed border-navy-200/30"
        style={{ width: radius * 2 + 80, height: radius * 2 + 80 }}
      />

      {/* Main orbit ring */}
      <div
        className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 rounded-full border-2 border-navy-100"
        style={{ width: radius * 2, height: radius * 2 }}
      />

      {/* Center element */}
      <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 z-10">
        <motion.div
          className="relative"
          animate={{ scale: [1, 1.05, 1] }}
          transition={{ duration: 3, repeat: Infinity, ease: 'easeInOut' }}
        >
          {/* Glow effect */}
          <div className="absolute inset-0 bg-gradient-to-br from-navy-400 to-navy-600 rounded-2xl blur-xl opacity-40" />

          {/* Main center box */}
          <div className="relative w-20 h-20 md:w-24 md:h-24 rounded-2xl bg-gradient-to-br from-navy-500 to-navy-700 shadow-2xl shadow-navy-500/30 flex items-center justify-center">
            <span className="text-white font-display font-bold text-2xl md:text-3xl">AI</span>
          </div>
        </motion.div>
      </div>

      {/* Rotating container */}
      <motion.div
        className="absolute top-1/2 left-1/2"
        animate={{ rotate: 360 }}
        transition={{
          duration,
          repeat: Infinity,
          ease: 'linear',
        }}
        style={{
          animationPlayState: isPaused ? 'paused' : 'running',
        }}
      >
        {techItems.map((item, index) => {
          const angle = (index * 360) / techItems.length
          const x = radius * Math.cos((angle * Math.PI) / 180)
          const y = radius * Math.sin((angle * Math.PI) / 180)
          const IconComponent = item.icon

          return (
            <motion.div
              key={item.name}
              className="absolute"
              style={{
                left: x,
                top: y,
                transform: 'translate(-50%, -50%)',
              }}
            >
              {/* Counter-rotate to keep items upright */}
              <motion.div
                animate={{ rotate: -360 }}
                transition={{
                  duration,
                  repeat: Infinity,
                  ease: 'linear',
                }}
                className="group"
              >
                <motion.div
                  whileHover={{ scale: 1.15 }}
                  className="relative w-12 h-12 md:w-14 md:h-14 rounded-xl bg-white border border-navy-100 shadow-lg flex items-center justify-center cursor-pointer hover:border-navy-300 hover:shadow-xl transition-all duration-300"
                >
                  <IconComponent />

                  {/* Tooltip */}
                  <div className="absolute -bottom-8 left-1/2 -translate-x-1/2 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none z-20">
                    <span className="text-xs font-medium text-navy-600 bg-white px-2 py-1 rounded-md shadow-lg whitespace-nowrap border border-navy-100">
                      {item.name}
                    </span>
                  </div>
                </motion.div>
              </motion.div>
            </motion.div>
          )
        })}
      </motion.div>

      {/* Decorative dots */}
      {[0, 45, 90, 135, 180, 225, 270, 315].map((angle) => (
        <div
          key={angle}
          className="absolute w-1.5 h-1.5 rounded-full bg-navy-300/40"
          style={{
            top: '50%',
            left: '50%',
            transform: `rotate(${angle}deg) translateX(${radius + 40}px) translateY(-50%)`,
          }}
        />
      ))}
    </div>
  )
}
